import { applicationContextForClient as applicationContext } from '../../../../../shared/src/business/test/createTestApplicationContext';
import { presenter } from '../../presenter';
import { runAction } from 'cerebral/test';
import { runTrialSessionPlanningReportAction } from './runTrialSessionPlanningReportAction';

describe('runTrialSessionPlanningReportAction', () => {
  beforeAll(() => {
    global.window = global;
    global.Blob = () => {};
    const fakeData =
      '';

    presenter.providers.applicationContext = applicationContext;
    presenter.providers.router = {
      createObjectURL: jest.fn().mockReturnValue('123456-abcdef'),
    };

    applicationContext
      .getUseCases()
      .runTrialSessionPlanningReportInteractor.mockResolvedValue(fakeData);
  });
  it('creates a pdf and returns an object URL', async () => {
    const result = await runAction(runTrialSessionPlanningReportAction, {
      modules: {
        presenter,
      },
      state: {
        modal: {
          term: 'winter',
          year: '2020',
        },
      },
    });
    expect(
      applicationContext.getUseCases().runTrialSessionPlanningReportInteractor,
    ).toHaveBeenCalled();
    expect(presenter.providers.router.createObjectURL).toHaveBeenCalled();
    expect(result.output).toHaveProperty('pdfUrl');
  });
});
